            <thead className="">
                <th> Id</th>
                <th> title</th>
                <th> content</th>
                <th> author</th>
                <th>Action</th>
            </thead>
            <tbody>
                {
                    props.props.map(
                        prop=>
                        <tr key={prop.id}>
                            <td>{prop.id}</td>
                            <td>{prop.title}</td>
                            <td>{prop.content}</td>
                            <td>{prop.author}</td>
                            <td>
                                <button className='btn btn-danger' onClick={()=>{
                                    PostService.deletePost(prop.id)
                                }} >Delete</button>

                            </td>

                        </tr>
                    )
                }
            </tbody>
        </table>
        </div>


     );
}

export default Post;
